@font-face {
    font-family: Modernist;
    src: url('../fonts/Sk-Modernist-Regular.e68f85ab30926114d85000538a0ee790.otf');
    font-display: swap;
}

@font-face {
    font-family: Sk-Modernist;
    src: url('../fonts/Sk-Modernist-Regular.e68f85ab30926114d85000538a0ee790.otf');
    font-display: swap;
}

@import url('https://fonts.googleapis.com/css2?family=Montserrat:wght@100;200;300;400;500;600;700;800;900&display=swap');

body {
    &.bodyblack {
        background: #000;
    }
}

.service-container {
    max-width: 100%;
    padding-left: 120px;
    padding-right: 120px;
    margin: 0 auto;
}

.serviserow {
    display: flex;
    flex-wrap: wrap;
    margin-left: -15px;
    margin-right: -15px;
}

.service__col-6 {
    padding-left: 15px;
    padding-right: 15px;
    width: 100%;
    max-width: 50%;
    flex: 0 0 50%;
}

.service-container {
    position: relative;
    z-index: 2;
}

.section-subtitle {
    background: linear-gradient(125.34deg, #CDFF7C 14.35%, #8CFFAC 54.63%, #93FFEB 101.27%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    margin-bottom: 32px;
    letter-spacing: 0.05em;
    text-transform: uppercase;
    font-weight: 400;
    font-size: 20px;
    line-height: 130%;
}

body.noscroll {
    overflow: hidden;
}

main {
    padding-top: 93px;
}


// ФІКС
.service-whatwedo .devservices:after {
    bottom: -280px;
}

@media(max-width:1700.8px) {
    .service-whatwedo .devservices:after {
        bottom: -280px;
    }
}

@media(max-width:1600.8px) {
    .service-whatwedo .devservices:after {
        bottom: -200px;
    }
}

@media(max-width:991.8px) {
    .service-whatwedo .devservices:after {
        width: 200px;
        height: 110px;
        bottom: -120px;
    }

    .service-whatwedo {
        padding-bottom: 140px;
    }
}

@media(max-width:767.8px) {
    .service-whatwedo .devservices:after {
        bottom: -50px;
    }

    .service-whatwedo {
        padding-bottom: 80px;
    }
}

.rowblock {
    display: flex;
    flex-wrap: wrap;
}

// Breadcrumbs

.breadcrumb {
    list-style-type: none;
    margin: 0;
    padding: 33px 0 0 0;

    li {
        display: inline-block;
        vertical-align: middle;
        font-size: 16px;
        color: #fff;
        font-family: "Montserrat", sans-serif;
        margin-right: 33px;
        position: relative;
        margin-bottom: 10px;

        &::after {
            content: '';
            display: inline-block;
            background: url('../images/services/breadcrumbs-arrow.svg') no-repeat center / 100%;
            width: 14px;
            height: 14px;
            position: absolute;
            top: 0;
            right: -25px;
        }

        &:last-child {
            margin-right: 0;

            &::after {
                display: none;
            }
        }

        a {
            color: #9D9D9E;
            transition: 0.3s;

            &:hover {
                color: #C9FA3D;
            }
        }

        &.active a {
            color: #fff;
        }
    }

    &.black {
        li {
            color: #9D9D9E;

            a {
                color: #222;

                &:hover {
                    color: #C9FA3D;
                }
            }
        }
    }
}

@media(max-width:1440.8px) {

    .breadcrumb li {
        margin-bottom: 20px;
        font-size: 14px;
    }



}
