header.header {
    position: fixed;
    width: 100%;
    left: 0;
    top: 0;
    z-index: 11;
    display: flex;
    align-items: center;
    height: 96px;
    padding: 10px 40px;
    transition: 0.5s;

    .langlist {
        display: none;
        z-index: 12;

        a {
            transition: 0.3s;
        }
    }

    nav {
        width: 100%;
        max-width: calc(100% - 600px);
        flex: 0 0 calc(100% - 600px);

        &>ul {
            list-style: none;
            margin: 0;
            padding: 0;
            display: flex;
            justify-content: center;
            align-items: center;

            &>li {
                position: relative;
                margin-right: 40px;

                &:last-child {
                    margin-right: 0;
                }

                &.dropdown {

                    &>a,
                    &>span {
                        position: relative;
                        padding-right: 20px;

                        svg {
                            position: absolute;
                            right: 0;
                            top: 4px;
                            transition: 0.3s;
                        }

                        &.rotate {
                            svg {
                                transform: rotate(-180deg);
                            }
                        }
                    }
                }

                &>a,
                &>span {
                    display: inline-block;
                    cursor: pointer;
                    color: #fff;
                    font-weight: 700;
                    font-size: 16px;
                    line-height: 150%;
                    letter-spacing: 1.05px;

                    background: linear-gradient(to right, white, white 50%, #8CFFAC 55%, #93FFEB);
                    -webkit-background-clip: text;
                    -webkit-text-fill-color: transparent;
                    background-size: 220% 100%;
                    transition: background-position 0.5s ease;

                    &:hover {
                        background-position: 100%;
                    }
                }
            }
        }
    }
}

.header__logo {
    width: 100%;
    max-width: 300px;
    flex: 0 0 300px;
    transition: 0.5s;

    img {
        max-width: 163px;
    }
}

.header__rightbox {
    width: 100%;
    max-width: 300px;
    flex: 0 0 300px;
    display: flex;
    align-items: center;
    justify-content: space-between;
}

.headerbtn {
    font-size: 16px;
    color: #C9FA3D;
    font-weight: 700;
    line-height: 120%;
    border: 0.5px solid #C9FA3D;
    display: inline-block;
    transition: 0.3s;
    padding: 12px 21px;

    svg {
        vertical-align: middle;
        margin-top: -3px;

        path {
            transition: 0.5s;
        }
    }

    &:hover {
        background: #0F0F10;
        box-shadow: 0px 8px 20px rgba(67, 253, 130, 0.25), 0px 26px 50px #2f463a;
    }

}

.megamenu {
    transition: 0.3s;
    visibility: hidden;
    opacity: 0;
    z-index: -1;
    width: 100%;
    left: 0;
    position: fixed;
    top: 56px;
    padding-top: 40px;
}

.megamenu.show {
    transition: 0.5s;
    visibility: visible;
    opacity: 1;
    z-index: 4;
}

.d-flex {
    display: flex;
    flex-wrap: wrap;
}

.megamenu__wrapper {
    background: #fff;
    padding: 24px 0 32px;
    max-height: calc(100vh - 96px);
    overflow: auto;
    box-shadow: 0 10px 10px #00000024;

    &::-webkit-scrollbar {
        width: 6px;
    }

    &::-webkit-scrollbar-track {
        box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.3);
    }

    &::-webkit-scrollbar-thumb {
        background-color: #a1a1a1;
        border-radius: 10px;
    }

    .listscolumn {
        width: 100%;
        max-width: 58%;
        flex: 0 0 58%;

        .title {
            margin-bottom: 16px;
            font-size: 14px;
            color: #141414;
        }
    }

    .slidercolumn {
        width: 100%;
        max-width: 42%;
        flex: 0 0 42%;
    }
}

.listscolumn {
    .mainlist {
        width: 100%;
        max-width: 41%;
        flex: 0 0 41%;
        border-right: 0.5px solid #C9FA3D;

        ul {
            list-style-type: none;

            li {
                position: relative;
                margin-bottom: 16px;

                &:last-child {
                    margin-bottom: 24px;
                }

                a {
                    color: #636B7F;
                    font-size: 14px;
                    font-weight: 700;
                    text-transform: capitalize;
                    transition: 0.3s;
                    line-height: 145%;

                    &::after {
                        content: '';
                        display: inline-block;
                        width: 16px;
                        height: 16px;
                        position: absolute;
                        left: 0;
                        top: 2px;
                        background: url('../images/header/mgmenuarr.svg') no-repeat center / 100%;
                        opacity: 0;
                        z-index: -1;
                        visibility: hidden;
                        transform: translateX(-10px);
                        transition: 0.3s;
                    }

                    &:hover {
                        color: #0F0F10;
                    }

                    &.active {
                        color: #0F0F10;
                        padding-left: 20px;

                        &::after {
                            opacity: 1;
                            z-index: 1;
                            visibility: visible;
                            transform: translateX(0);
                        }
                    }
                }
            }
        }
    }

    .sidelists {
        width: 100%;
        max-width: 59%;
        flex: 0 0 59%;
        padding-left: 64px;

        ul {
            display: none;
            list-style: none;
            margin: 0;
            padding: 0;

            &.show {
                display: block;
            }

            li {
                margin-bottom: 16px;

                a {
                    font-size: 16px;
                    color: #0F0F10;
                    line-height: 145%;

                    &:hover {
                        text-decoration: underline;
                        text-underline-offset: 2px;
                    }
                }
            }
        }
    }
}

.mgmenubottbtn {
    width: 100%;
    max-width: 100%;
    flex: 0 0 100%;
    margin-top: 16px;

    a {
        font-size: 16px;
        color: #0F0F10;
        line-height: 120%;
        min-height: 46px;
        max-width: 260px;
        width: 100%;
        background: #fff;
        border: 1px solid #000;
        display: inline-flex;
        align-items: center;
        justify-content: center;
        font-weight: 700;
        transition: 0.3s;

        svg {
            position: relative;
            margin-left: 4px;
        }

        &:hover {
            background: #C9FA3D;
            border-color: #C9FA3D;
        }
    }
}

// SECOND MEGAMENU
.dealslistscolumn {
