                //     content: "";
                //     display: inline-block;
                //     width: 16px;
                //     height: 16px;
                //     position: absolute;
                //     left: 0;
                //     top: 0;
                //     background: url(../images/header/mgmenuarr.svg) no-repeat center / 100%;
                //     opacity: 0;
                //     z-index: -1;
                //     visibility: hidden;
                //     transform: translateX(-10px);
                //     transition: 0.3s;
                // }

                &:hover {
                    color: #0F0F10;
                }

                // .active {
                //     color: #0F0F10;
                //     padding-left: 20px;

                //     &::after {
                //         opacity: 1;
                //         z-index: 1;
                //         visibility: visible;
                //         transform: translateX(0);
                //     }
                // }
            }
        }
    }
}

.dealscolumn {
    width: 100%;
    max-width: 55%;
    flex: 0 0 55%;
    text-align: right;

    .dealscolumn__wrap {
        text-align: left;
        max-width: 685px;
        display: inline-block;
    }

    .title {
        margin-bottom: 16px;
        font-size: 14px;
        color: #141414;
    }

    .headerdeals {
        display: flex;
        flex-wrap: wrap;
        align-items: flex-start;
        justify-content: space-between;

        .headerdeal {
            width: 100%;
            max-width: calc(25% - 10px);
            flex: 0 0 calc(25% - 10px);
            height: 180px;
            position: relative;
            overflow: hidden;
            border-radius: 5px;
            transition: 0.3s;
            box-shadow: 0 2px 12px rgba(#000, 0.1);

            &:hover {
                box-shadow: 6px 6px 26px rgba(187, 44, 212, 0.15);
            }

            img {
                width: 100%;
                height: 100%;
                object-fit: cover;
                object-position: 50% 50%;
            }

            p {
                position: absolute;
                left: 0;
                bottom: 0;
                width: 100%;
                margin: 0;
                font-size: 12px;
                font-weight: 700;
                line-height: 112%;
                color: #fff;
                z-index: 2;
                text-align: center;
                padding: 5px 5px 9px 5px;
            }
        }
    }


}

// header SLIDER
.slidercolumn {
    text-align: right;
}

.headerslider {
    max-width: 556px;
    display: inline-block;
    width: 100%;

    .slick-slide {
        padding: 20px;
    }

    .slick-dots {
        list-style: none;
        text-align: center;
        margin-top: -15px;

        li {
            margin: 0 4px;
            display: inline-block;
            vertical-align: middle;

            button {
                font-size: 0;
                padding: 0;
                width: 10px;
                height: 10px;
                border-radius: 50%;
                border: none;
                background: #5A15CB;
                opacity: 0.2;
                transition: 0.3s;
            }
        }

        li.slick-active {
            button {
                opacity: 1;
            }
        }
    }

    .headerslider__slide {
        height: 200px;
        width: 100%;
        position: relative;
        display: flex;
        align-items: flex-end;
        justify-content: center;
        border-radius: 6px;
        overflow: hidden;
        transition: 0.3s;

        &:hover {
            box-shadow: 6px 6px 26px rgba(187, 44, 212, 0.15);
        }

        img {
            position: absolute;
            left: 0;
            top: 0;
            object-fit: cover;
            object-position: 50% 50%;
            border-radius: 4px;
            width: 100%;
            height: 100%;
        }

        p {
            position: relative;
            z-index: 2;
            color: #fff;
            text-align: center;
            padding: 10px 10px 4px 10px;
            text-transform: capitalize;
            font-size: 16px;
            font-weight: 800;
            line-height: 135%;
            max-width: 100%;
            flex: 0 0 100%;
        }
    }
}

// WHITE header
.header.header {

    &.white,
    &.whitedefault {
        background: #fff !important;

        nav>ul>li>a,
        nav>ul>li>span {
            background: linear-gradient(to right, #212121, #212121 30%, #8CFFAC 40%, #93FFEB 65%, #a1a1a1 65%);
            -webkit-background-clip: text;
            -webkit-text-fill-color: transparent;
            background-size: 330% 100%;
        }

        nav>ul>li>a,
        nav>ul>li>span {
            background: linear-gradient(to right, #212121, #212121 30%, #8CFFAC 40%, #93FFEB 65%, #a1a1a1 65%);
            -webkit-background-clip: text;
            -webkit-text-fill-color: transparent;
            background-size: 330% 100%;

            &:hover {
                background-position: 100% 0;
            }
        }

        .header__logo {
            // filter: invert(1);
        }

        .headerbtn {
            color: #0F0F10;
            border-color: #0F0F10;

            svg path {
                stroke: #0F0F10;
            }

            &:hover {
                box-shadow: none;
                background: #C9FA3D;
                border-color: #C9FA3D;

                svg path {}
            }
        }

        nav>ul>li.dropdown>a,
        nav>ul>li.dropdown>span {
            &>svg {
                path {
                    stroke: #212121;
                }
            }

            &:hover {
                path {
                    stroke: #a1a1a1;
                }
            }
        }

        .header__language span {
            background: linear-gradient(to right, #212121, #212121 55%, #8CFFAC 54%, #93FFEB);
            -webkit-background-clip: text;
            -webkit-text-fill-color: transparent;
        }

        .header__burger span {
            background: #0F0F10;
        }
    }

    &.headeropacbg {
        background: rgba(0, 0, 0, 0.9);
    }
}

// BLACK HEADER
.header.header {
    &.black {
        background-color: #0A0A0B;
    }
}

// Header baner
.header.header {
    &.support {
        top: 40px;

        .megamenu {
            top: 96px;
        }
    }
}

.supua {
    height: 40px;
    background: #3663D8;
    width: 100%;
    position: relative;
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    align-items: center;
    justify-content: center;
    text-align: center;
    color: #fff;
    text-transform: uppercase;
    font-weight: 700;
    font-size: 20px;
    line-height: 140%;
    transition: 0.3s;

    p {
        margin: 0;
        display: flex;
        align-items: center;

        img {
            max-width: 42px;
            margin: 0 20px;
        }

        span.ltext {
            font-size: 12px;
            line-height: 15px;
            font-family: "Montserrat", sans-serif;
            position: relative;
            display: flex;
            align-items: center;
            margin: 0 20px;
        }

        .supua__link {
            display: inline-block;
            padding: 3px 31px;
            color: #fff;
            text-decoration: none;
            background: #F2B14F;
            border-radius: 43px;
            font-weight: 700;
            font-size: 16px;
            line-height: 20px;
            font-family: "Montserrat", sans-serif;
            transition: 0.3s;
        }
    }
}

.header__burger,
.headermobilebtn,
.tablet-sublist,
.tabletarrow,
.tabletmenu {
    display: none;
}

header.header.white,
header.header.whitedefault {
    border-bottom: 0.5px solid #C9FA3D;
}

.slidercolumn {
    padding-top: 10px;
}

.dealslistscolumn ul li a {
    line-height: 145%;
}

header.header {
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
}

body * {
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
}

footer.footer * {
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
}

.footer__country a,
.footer__info .mainemail {
    -webkit-user-select: text;
    -moz-user-select: text;
    -ms-user-select: text;
    user-select: text;
}



@media(max-width:1600.8px) {
    header.header nav>ul>li {
        margin-right: 26px;

        &>a,
        &>span {
            font-size: 15px;
        }
    }

    .listscolumn .sidelists {
        padding-left: 60px;
    }

    .listscolumn .sidelists ul li {
        margin-bottom: 10px;

        a {
            font-size: 14px;
        }
    }

