    .listscolumn .mainlist {
        max-width: 52%;
        flex: 0 0 52%;
    }

    .listscolumn .sidelists {
        max-width: 48%;
        flex: 0 0 48%;
        padding-left: 64px;
    }

    .headerslider {
        max-width: 461px;
    }

    .dealscolumn .dealscolumn__wrap {
        max-width: 644px;
    }



}

@media(max-width:1400.8px) {
    header.header nav>ul>li {
        margin-right: 15px;
    }

    header.header nav>ul>li>a,
    header.header nav>ul>li>span {
        font-weight: 500;
    }

    .header__logo {
        max-width: 200px;
        flex: 0 0 200px;
    }

    .header__rightbox {
        max-width: 280px;
        flex: 0 0 280px;
    }

    header.header nav {
        max-width: calc(100% - 480px);
        flex: 0 0 calc(100% - 480px);
    }

    .header__language {
        margin-right: 0 !important;
    }

    .listscolumn .mainlist {
        padding-right: 15px;

        ul li a {
            display: block;
        }
    }

    .headerslider .headerslider__slide p {
        font-size: 18px;
    }

    .headerslider .headerslider__slide {
        height: 200px;
    }

    .dealslistscolumn {
        padding-right: 15px;
    }

    .dealscolumn {
        padding-left: 30px;
    }

}

@media(max-width:1280.8px) {
    .header__logo img {
        max-width: 108px;
    }

    header.header nav>ul>li>a,
    header.header nav>ul>li>span {
        font-size: 14px;
        font-weight: 700;
    }

    .header.header.white .header__language span,
    .header.header.whitedefault .header__language span {
        font-size: 14px;
    }

    header.header .header__language span:after {
        top: -2px;
    }

    header.header {
        height: 86px;
    }

    header.header nav>ul>li {
        margin-right: 32px;
    }

    .header__logo {
        max-width: 160px;
        flex: 0 0 160px;
    }

    header.header nav {
        max-width: calc(100% - 440px);
        flex: 0 0 calc(100% - 440px);
    }

    .megamenu {
        padding-top: 30px;
    }

    header.header {
        .service-container {
            padding-left: 40px;
            padding-right: 40px;
        }
    }

    .megamenu__wrapper {
        padding: 16px 0 32px;
    }

    .listscolumn .sidelists {
        padding-left: 60px;
    }

    .headerslider .slick-dots li button {
        width: 8px;
        height: 8px;
    }

    .headerslider .slick-dots li {
        margin: 0 2px;
    }

    .headerslider .slick-slide {
        padding: 10px;
    }

    .dealscolumn .headerdeals .headerdeal {
        height: 174px;
    }

    .dealscolumn .headerdeals .headerdeal p {
        br {
            display: none;
        }
    }

    main {
        padding-top: 86px;
    }

    .listscolumn .mainlist {
        max-width: 57%;
        flex: 0 0 57%;
    }

    .listscolumn .sidelists {
        max-width: 43%;
        flex: 0 0 43%;
    }

    .headerslider {
        max-width: 404px;
    }

    .headerslider .slick-dots {
        margin-top: -8px;
    }

    .slidercolumn {
        padding-top: 20px;
    }

}

@media(max-width:1199.8px) {

    header.header.white,
    header.header.whitedefault {
        border-bottom: none;
    }

    .header__rightbox .headerbtn {
        display: none;
    }

    .header__burger {
        width: 22px;
        max-width: 22px;
        flex: 0 0 22px;
        height: 18px;
        display: -webkit-box;
        display: -ms-flexbox;
        display: flex;
        flex-direction: column;
        justify-content: space-between;
        position: relative;
        align-items: flex-end;
        right: 40px;
        position: absolute;

        span {
            background: #fff;
            display: inline-block;
            height: 2px;
            width: 100%;
            transition: 0.5s;

            &:nth-child(2) {
                width: 60%;
            }
        }

        &.open {
            span {
                &:nth-child(1) {
                    transform: rotate(45deg) translate(6px, 5px);
                }

                &:nth-child(2) {
                    opacity: 0;
                    transform: translateX(10px);
                }

                &:nth-child(3) {
                    transform: rotate(-45deg) translate(6px, -5px);
                }

            }
        }
    }

    header.header nav {
        overflow: auto;
        opacity: 0;
        z-index: -1;
        visibility: hidden;
        transition: 0.5s;
        display: block;
        transform: translateY(-10px);
        padding-bottom: 40px;

        &.show {
            opacity: 1;
            z-index: 1;
            visibility: visible;
            position: fixed;
            left: 0;
            top: 86px;
            width: 100%;
            height: calc(100vh - 86px);
            background: #fff;
            max-width: initial;
            transform: translateY(0);
            border-top: 0.5px solid #C9FA3D;
        }


    }

    .header.header.white nav.show>ul,
    .header.header.whitedefault nav.show>ul {
        display: block;
        padding: 40px;

        &>li {
            margin: 0 0 15px 0;

            &>a,
            &>span {
                font-size: 21px;
                font-weight: 600;

                svg {
                    top: 8px;
                }
            }
        }
    }

    .megamenu {
        position: relative;
        top: 0;
        padding: 0;
        visibility: visible;
        opacity: 1;
        z-index: 1;
        display: none;

        .service-container {
            padding: 0 !important;
        }

        &.show {
            display: block;
        }
    }

    .megamenu__wrapper {
        padding-bottom: 20px;
    }

    .header__rightbox {
        max-width: 150px;
        flex: 0 0 150px;
    }

    header.header {
        justify-content: space-between;

        nav {
            max-width: calc(100% - 350px);
            flex: 0 0 calc(100% - 350px);
        }
    }

    .headermobilebtn {
        display: block;
        padding: 0 40px;

        a {
            min-width: 260px;
            text-align: center;
        }
    }

    .megamenu__wrapper {
        max-height: initial;
    }

    .headermobilebtn .headerbtn {
        // background: transparent !important;

        &:hover {
            box-shadow: none;
        }
    }

    .header.header {
        &.support {
            top: 40px;

            nav.show {
                top: 136px;
                height: calc(100vh - 136px);
            }
        }

    }

    .header.header.support .megamenu {
        top: 0;
    }

    .header.header.white .headerbtn:hover,
    .header.header.whitedefault .headerbtn:hover {
        color: #0F0F10;

        svg path {
            stroke: #0F0F10;
        }
    }

    .megamenu__wrapper {
        box-shadow: none;
    }

    .mgmenubottbtn a:hover {
        box-shadow: none;
    }

    .listscolumn .sidelists {
        padding-left: 30px;
    }




}

@media(max-width:1100.8px) {
    .headerslider .slick-slide {
        padding: 0 2px 10px 2px;
    }

    .megamenu__wrapper .slidercolumn {
        padding-left: 50px;
