.welcomesection {
    height: 100vh;
    position: relative;
    display: flex;
    align-items: center;
    min-height: 850px;

    &::after {
        content: '';
        display: block;
        position: absolute;
        left: 0;
        top: 0;
        width: 100%;
        height: 100%;
        z-index: 1;
        background: #0F0F10;
        opacity: 0.5;
    }

    .service-container {
        width: 100%;
    }
}


.welcomesection__bg {
    width: 100%;
    height: 100%;
    position: absolute;
    left: 0;
    top: 0;
    object-fit: cover;
    object-position: 50% 50%;
}

.welcomesection__content {
    position: relative;
    z-index: 2;
    font-family: 'Montserrat';
    margin-top: -45px;
}

.welcomesection__title {
    color: #FFFFFF;
    font-weight: 700;
    font-size: 80px;
    line-height: 104%;
    max-width: 956px;
    margin-bottom: 33px;

    span {
        display: block;
        background: linear-gradient(125.34deg, #CDFF7C 14.35%, #8CFFAC 54.63%, #93FFEB 101.27%);
        -webkit-background-clip: text;
        -webkit-text-fill-color: transparent;
    }
}

.welcomesection__descr {
    max-width: 344px;
    margin-bottom: 27px;

    p {
        font-size: 16px;
        line-height: 130%;
        color: #fff;

        &:last-child {
            margin-bottom: 0;
        }
    }
}

.welcomesection__link {
    display: inline-block;
    padding: 14px 32px;
    background: #fff;
    color: #000000;
    letter-spacing: 0.05em;
    font-weight: 700;
    font-size: 16px;
    line-height: 130%;
    transition: 0.5s;
    position: relative;

    &:hover {
        animation: pulse1 2s infinite;
    }
}

@keyframes pulse1 {
    0% {
        -webkit-box-shadow: 0 0 0 0 rgba(255, 255, 255, .2);
    }

    70% {
        -webkit-box-shadow: 0 0 0 20px rgba(255, 255, 255, 0);
    }

    100% {
        -webkit-box-shadow: 0 0 0 0 rgba(255, 255, 255, .2);
    }
}

.welcomesection__bottomsect {
    position: absolute;
    z-index: 2;
    width: 100%;
    left: 0;
    bottom: 75px;


    .service-container {
        width: 100%;
    }

    a {
        display: inline-block;
        margin-right: 45px;
    }

    .welcomesection__bottcont {
        display: flex;
        justify-content: flex-end;
        align-items: center;
    }

}

.welcomesection__bottcont a {}

.welcomesection__bottcont a:not(:last-child) {
    position: relative;
    height: 80px;
    width: 95px;
    display: flex;
    align-items: center;
    justify-content: center;
}

.welcomesection__bottcont a:last-child {
    height: 80px;
    display: flex;
    align-items: center;
    justify-content: center;
    margin-right: 0;
}

.defaultimg {
    opacity: 0.6;
}

.hoverimg {
    display: none;
}

.welcomesection__bottcont a:hover {
    .defaultimg {
        display: none;
    }

    .hoverimg {
        display: block;
        animation: hoverbadge 2s infinite;
    }
}

.devservivessection {
    // background: linear-gradient(180deg, #0A0A0B 0%, #0E0E0F 100%);
    background: #0A0A0B;
    padding: 208px 0 145px;
    position: relative;

    .service-faq__titles {
        margin-bottom: 104px;
    }

    .abussectitle {
        max-width: 1182px;
    }

    &::after {
        content: "";
        position: absolute;
        left: 0;
        top: 0;
        width: 100%;
        height: 100%;
        // background-image: url(../images/grain.png), url(../images/newmain/grids1.png);
        background-image: url(../images/newmain/grid.svg);
        background-repeat: no-repeat, no-repeat;
        // background-size: 100% 100%, 100% 100%;
        // background-position: 0 0, 0 -1px;
        background-size: 100% auto;
        background-position: 0 -1px;
        z-index: 1;
        opacity: 0.7;
    }

    &::before {
        content: "";
        display: block;
        position: absolute;
        left: 0;
        right: 0;
        top: 103px;
        width: 100%;
        max-width: 1640px;
        margin: 0 auto;
        height: 924px;
        max-height: 100%;
        z-index: 2;
        background: radial-gradient(54.07% 52.6% at 36.53% 34.34%, #0D0D0E 44.21%, rgba(32, 29, 29, 0) 100%);
        opacity: 0.8;
        filter: blur(16px);
    }

    .service-container {
        position: relative;
        z-index: 3;
    }

}

.devservives-row {
    display: flex;
    flex-wrap: wrap;
    justify-content: space-between;
}

.devservivesbox {
    max-width: 372px;
    width: 100%;
    padding: 24px;
    border: 1px solid;
    border-image-slice: 1;
    border-width: 1px;
    border-image-source: linear-gradient(125.34deg, #CDFF7C 14.35%, #8CFFAC 54.63%, #93FFEB 101.27%);
    min-height: 388px;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    background: #141415;

    .title {
        margin-bottom: 16px;
        color: #fff;
        font-weight: 700;
        font-size: 24px;
        line-height: 135%;
        font-family: 'Montserrat';
        display: block;
        // display: -webkit-box;
        // -webkit-line-clamp: 3;
        // -webkit-box-orient: vertical;
        // overflow: hidden;
    }

    .descr {
        display: -webkit-box;
        -webkit-line-clamp: 5;
        -webkit-box-orient: vertical;
        overflow: hidden;
        color: #fff;
        font-weight: 500;
        font-size: 16px;
        line-height: 180%;
    }

    .devservivesbox__btn {
        a {
            display: flex;
            align-items: center;
            justify-content: center;
            height: 67px;
            border: 1px solid;
            border-image-slice: 1;
            border-width: 1px;
            border-image-source: linear-gradient(125.34deg, #CDFF7C 14.35%, #8CFFAC 54.63%, #93FFEB 101.27%);
            color: #C9FA3D;
            letter-spacing: 0.02em;
            text-align: center;
            font-weight: 700;
            font-size: 18px;
            transition: 0.3s;

            &:hover {
                background: #C9FA3D;
                color: #0F0F10;
            }
        }
    }

}

