@font-face {
    font-family: "Montserrat-Bold";
    src: url("../fonts/FontsFree-Net-Montserrat-ExtraBold.ttf");
    font-weight: normal;
    font-style: normal;
}


.welcomesection__bottcont-left p a,
.aboutus-welcomesect .gradientlink,
.dropusanote.dropusanote__hupd .dropusanotesect__content .dropusanotelink a span,
.hupd-benefits__left .getproduct {
    // background: linear-gradient(125.34deg,white 50%, #CDFF7C 64.35%,#8CFFAC 74.63%,#93FFEB 101.27%);
    background: linear-gradient(125.34deg, #CDFF7C 0%, #8CFFAC 30.63%, #93FFEB 51%, white 50%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-size: 250% 100%;
    transition: background-position 0.5s ease;
    background-position: 0 0;

    &:hover {
        background-position: 100% 0;
    }
}



.welcomesection__link:hover,
.service-testimonials .servtestim__header .servtestim__header-right a:hover,
.lateststories__header-right a:hover {
    box-shadow: 0px 8px 20px rgb(67 253 130 / 25%), 0px 26px 50px #2f463a;
    animation: none;
    background: #C9FA3D;
    transition: 0.3s;
}

// fff > gr
header.header .header__language span,
.hupd-benefits__left .getproduct,
.devservivessection.devservivessection__hupd .title {
    font-weight: 400;
    font-family: "Montserrat-Bold";
}

