    font-weight: 500 !important;
}

.devservivessection.devservivessection__hupd .title,
.dropusanote .dropusanotelink a span,
header.header .header__language span {
    color: #fff;
    transition: 0.3s;
    background: linear-gradient(to right, white, white 55%, #8CFFAC 54%, #93FFEB);
    background-clip: text;
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-size: 222% 100%;
    background-position: 0%;
    -webkit-transition: background-position 0.5s ease;
    transition: background-position 0.42s ease;

    &:hover {
        background-position: 100%;
    }
}



