.scheduleacall {
    background: #C9FA3D;
    width: 237px;
    height: 52px;
    position: fixed;
    right: 0;
    top: 50%;
    z-index: 15;
    display: flex;
    align-items: center;
    padding: 0 16px;
     transform: rotate(-90deg);
    transform-origin: 100% 100%;
    transition: 0.3s;
    margin-top: -144px;

    span {
        color: #0F0F10;
        font-size: 18px;
        font-weight: 700;
        line-height: 130%;
        letter-spacing: 0.36px;
        font-family: 'Montserrat';
        width: 100%;
        max-width: calc(100% - 40px);
        flex: 0 0 calc(100% - 40px);
    }

    .imgwrap {
        width: 100%;
        max-width: 40px;
        flex: 0 0 40px;
        text-align: right;
        border-left: 1px solid #0F0F10;

        img {
            width: 24px;
            transform: rotate(90deg);
        }
    }

    &:hover {
        box-shadow: 0px 0px 20px rgba(67,253,130,0.25), 0px 0 50px #2f463a;
    }

   
}

@media(max-width:575.8px) {
    .scheduleacall span {
        display: none;
    }

    .scheduleacall .imgwrap {
        border: none;
        width: 100%;
        height: 100%;
        max-width: 100%;
        flex: 0 0 100%;
        display: flex;
        align-items: center;
        justify-content: center;
    }

    .scheduleacall {
        width: 48px;
        height: 48px;
        padding: 0;
        margin-top: 0;
        top: initial;
        bottom: 0;
        transform: none;
    }

    .scheduleacall .imgwrap {
        img {
            transform: none;
        }
    }
}
