                    text-decoration: none;
                    width: 44px;
                    height: 44px;
                    background: #fff;
                    display: inline-flex;
                    align-items: center;
                    justify-content: center;
                    border-radius: 50%;

                    img {
                        width: 20px;
                    }
                }
            }
        }

        .arrow {
            width: 100%;
            max-width: 20px;
            flex: 0 0 20px;
            opacity: 0.5;
            cursor: pointer;

            img {
                width: 100%;
            }
        }

        .userlink {

            a {
                width: 100%;
                height: 100%;
                overflow: hidden;

                img {
                    width: 100%;
                    height: 100%;
                    object-fit: cover;
                    object-position: 50% 50%;
                }
            }
        }
    }

    .articlestickybox__open {
        top: 40px;
        transition: 0.5s;
        position: absolute;
        opacity: 0;
        z-index: -1;

        div {

            vertical-align: middle;
            display: inline-flex;
            align-items: center;
            justify-content: center;
            width: 48px;
            height: 48px;
            background: #D8D8D8;
