            span {
                text-decoration: none;
                width: 44px;
                height: 44px;
                background: #fff;
                display: inline-flex;
                align-items: center;
                justify-content: center;
                border-radius: 50%;
                cursor: pointer;

                img {
                    width: 20px;
                }
            }
        }
    }

    &.hiddenelms {
        width: 103px;
        height: 80px;

        .articlestickybox__info {
            opacity: 0;
            top: -40px;

            &* {
                white-space: nowrap;
            }
        }

        .articlestickybox__open {
            top: 16px;
            opacity: 1;
            z-index: 1;
        }

    }
}

