commits.content:
.serviceswelcome {
    padding: 88px 0 135px;
    font-family: 'Montserrat',
        sans-serif;
}

.serviceswelcome__left {
    h1 {
        color: #fff;
        font-size: 80px;
        font-weight: 700;
        line-height: 120%;
        margin-bottom: 40px;

        span {
            color: #C9FA3D;
        }
    }

    p {
        max-width: 738px;
        color: #fff;
        font-size: 18px;
        font-weight: 500;
        line-height: 200%;
        opacity: 0.7;
        margin-bottom: 40px;
    }

    a {
        display: inline-block;
        background: #C9FA3D;
        transition: 0.3s;
        color: #0F0F10;
        font-size: 18px;
        font-weight: 700;
        line-height: 130%;
        letter-spacing: 0.36px;
        padding: 22px 44px;

        &:hover {
            box-shadow: 0px 8px 20px rgba(67, 253, 130, 0.25), 0px 26px 50px #2f463a;
        }
    }
}

.slinfwrap {
    display: flex;
    flex-wrap: wrap;
    margin-left: -8px;
    margin-right: -8px;

    .slinf {
        padding-left: 8px;
        padding-right: 8px;
        width: 100%;
        max-width: 38%;
        flex: 0 0 38%;
    }

    .srinf {
        padding-left: 8px;
        padding-right: 8px;
        width: 100%;
        max-width: 62%;
        flex: 0 0 62%;
    }

    .col-6 {
        padding-left: 8px;
        padding-right: 8px;
        width: 100%;
        max-width: 50%;
        flex: 0 0 50%;
    }

    .col-12 {
        padding-left: 8px;
        padding-right: 8px;
        width: 100%;
        max-width: 100%;
        flex: 0 0 100%;
    }
}

.serviceswelcome.lightblue {
    .serviceswelcome__left h1 span {
        background: linear-gradient(180deg, #CDFFCF 0%, #A6E9FF 100%);
        background-clip: text;
        -webkit-background-clip: text;
        -webkit-text-fill-color: transparent;
    }

    .servwelinfobox .title {
        color: #fff;
    }

    .servwelinfobox.horlong {
        background: linear-gradient(180deg, #CDFFCF 0%, #A6E9FF 100%);
        box-shadow: 6px 5px 18px 0px #000;
    }
}


.servwelinfobox {
    padding: 36px;
    border-radius: 24px;
    border: 1px solid rgba(219, 255, 116, 0.20);
    background: rgba(15, 15, 16, 0.90);
    box-shadow: 6px 5px 18px 0px #000;
    text-align: center;
    overflow: hidden;
    height: 206px;

    .title {
        color: #C9FA3D;
        font-size: 56px;
        font-weight: 700;
        line-height: 135%;
    }

    .descr {
        color: #FFF;
        font-size: 16px;
        font-weight: 500;
        line-height: 180%;
    }

    &.vertlong {
        height: 514px;

        img {
            margin-top: 32px;
            margin-bottom: 64px;
            max-width: 100%;
            max-height: 230px;
        }
    }

    &.horlong {
        margin-top: 16px;
        height: 292px;
        background: linear-gradient(135deg, #C9FA3D 0%, #4AFF88 100%);
        padding: 24px 36px;
        display: flex;
        flex-direction: column;
        justify-content: space-between;
    }

    .horlongheader {
        display: flex;
        flex-wrap: wrap;
        align-items: center;

        span {
            color: #0F0F10;
            font-size: 56px;
            font-weight: 700;
            line-height: 135%;
            width: 100%;
            max-width: 150px;
            flex: 0 0 150px;
        }

        p {
            color: #0F0F10;
            font-size: 16px;
            font-weight: 500;
            line-height: 180%;
            width: 100%;
            max-width: calc(100% - 150px);
            flex: 0 0 calc(100% - 150px);
            text-align: left;
            padding-left: 8px;
        }
    }
}

.hupd-clientfutured.services {
    padding: 104px 0;
    background: rgba(10, 10, 11, 0.7);
    box-shadow: 0px 4px 52px 0px rgba(219, 255, 116, 0.10);
    font-family: 'Montserrat', sans-serif;

    .service-container {
        padding-right: 0;
    }

    .clientfutured__imgs .slick-slide {
        margin: 0 70px;
    }

    .hupd-clientfutured__left {
        max-width: 440px;
        flex: 0 0 440px;

        p {
            font-size: 35px;
            line-height: 140%;
        }
    }

    .hupd-clientfutured__right {
        max-width: calc(100% - 440px);
        flex: 0 0 calc(100% - 440px);
    }
}

.servappssect {
    padding: 158px 0 294px;
    font-family: 'Montserrat', sans-serif;

    h2 {
        color: #fff;
        text-align: center;
        font-size: 56px;
        font-weight: 700;
        line-height: 135%;
        margin-bottom: 88px;
    }

    .serviserow {
        margin-left: -8px;
        margin-right: -8px;
    }

    .servappssect__leftcol {
        width: 100%;
        max-width: 66.333%;
        flex: 0 0 66.333%;
        padding-left: 8px;
        padding-right: 8px;

        a {
            display: block;
            margin-bottom: 16px;

            &:last-child {
                margin-bottom: 0;
            }
        }
    }

    .servappssect__rightcol {
        width: 100%;
        max-width: 33.333%;
        flex: 0 0 33.333%;
        padding-left: 8px;
        padding-right: 8px;
    }
}

.servappbox {
    padding: 48px 104px 24px;
    border: 1px solid rgba(219, 255, 116, 0.20);
    background: rgba(15, 15, 16, 0.90);
    box-shadow: 6px 5px 18px 0px #000;
    transition: 0.3s;
    height: 540px;
    overflow: hidden;

    .title {
        font-size: 32px;
        font-weight: 700;
        line-height: 140%;
        background: linear-gradient(125.34deg, #CDFF7C 14.35%, #8CFFAC 54.63%, #93FFEB 101.27%);
        -webkit-background-clip: text;
        -webkit-text-fill-color: transparent;
        margin-bottom: 30px;

        img {
            vertical-align: middle;
            margin-top: -3px;
        }
    }

    .appinfowrap {
        display: flex;
        flex-wrap: wrap;

        .imgwrap {
            max-width: 68%;
            flex: 0 0 68%;
            border-radius: 5px;
            box-shadow: 0px 24px 160px 0px rgba(0, 0, 0, 0.25);
            overflow: hidden;

            img {
                width: 100%;
            }
        }

        .descrwrap {
            max-width: 100%;
            max-width: 32%;
            flex: 0 0 32%;
            padding-left: 60px;
            padding-top: 15px;

            .descrbox {
                margin-bottom: 32px;

                &:last-child {
                    margin-bottom: 0;
                }

                p {
                    color: #fff;
                    font-size: 14px;
                    font-weight: 500;
                    line-height: 160%;
                    margin: 0;
                }

                .descrbox__title {
                    margin-bottom: 4px;
                    font-size: 32px;
                    font-weight: 700;
                    line-height: 140%;
                }
            }
        }
    }

    &:hover {
        box-shadow: 6px 5px 24px 0px rgba(226, 252, 88, 0.35);
        border-color: #8CFFAC;
    }

    &.vertical {
        text-align: center;
        padding: 48px 100px;
        height: 867px;

        .appinfowrap .imgwrap {
            max-width: 100%;
            flex: 0 0 100%;
            margin-bottom: 16px;
        }

        .servappbox .title {
            margin-bottom: 48px;
        }

        .appinfowrap .descrwrap {
            max-width: 100%;
            flex: 0 0 100%;
            padding: 0;
        }

        .appinfowrap .descrwrap .descrbox {
            p {
                font-size: 18px;
            }

            .descrbox__title {
                font-size: 32px;
            }
        }
    }

}

.vievallcases {
    border: 1px solid rgba(255, 255, 255, 0.20);
    background: var(--primary-lime, #C9FA3D);
    box-shadow: 6px 5px 18px 0px #000;
    display: flex;
    align-items: center;
    height: 212px;
    margin-top: 16px;
    padding: 20px 64px;
    color: #0F0F10;
    font-size: 56px;
    font-weight: 700;
    line-height: 135%;
    transition: 0.3s;

    &:hover {
        box-shadow: 0px 8px 20px rgba(67, 253, 130, 0.25), 0px 26px 50px #2f463a;
    }

    span {
        width: 100%;
        max-width: calc(100% - 80px);
        flex: 0 0 calc(100% - 80px);
        text-align: right;
        padding-right: 36px;
    }

    img {
        width: 100%;
        max-width: 80px;
        flex: 0 0 80px;
    }
}

.shareidea__wrap {
    position: relative;
    padding: 64px 120px;
    border-radius: 24px;
    border-top: 1px solid rgba(219, 255, 116, 0.20);
    border-bottom: 1px solid rgba(219, 255, 116, 0.20);
    border-left: 1px solid rgba(219, 255, 116, 0.20);
    background: rgba(10, 10, 11, 0.7);
    box-shadow: 6px 5px 18px 0px #000;
    font-family: 'Montserrat', sans-serif;

    .shareideapic1 {
        position: absolute;
        left: -80px;
        top: -100px;
        width: 226px;
    }

    .shareideapic2 {
        position: absolute;
        right: -40px;
        top: -3px;
        width: 218px;
    }

    .shareideapic3 {
        position: absolute;
        left: 47%;
        bottom: -100px;
        width: 270px;
    }

    .shareideapic4 {
        position: absolute;
        bottom: 0;
        right: 180px;
    }





}

.shareidea {
    padding-bottom: 174px;
}

.shareidea__form {
    max-width: 622px;
    width: 100%;

    .title {
        color: #fff;
        font-size: 32px;
        font-weight: 700;
        line-height: 140%;
        text-align: center;
        margin-bottom: 40px;

        span {
            color: #C9FA3D;
        }
    }

    .inputfield {
        margin-bottom: 46px;

        input,
        textarea {
            height: 42px;
            background: transparent;
            width: 100%;
            border: none;
            border-bottom: 1px solid #E7EAEC;
            font-size: 16px;
            color: #D8D8D8;
            font-weight: 600;
            letter-spacing: 0.32px;
            font-family: 'Montserrat', sans-serif;
            outline: none;
            transition: 0.3s;

            &:focus {
                border-color: #C9FA3D;
            }
        }

        textarea {
            padding-top: 10px;
            resize: none;
        }
    }

    .submform {
        height: 67px;
        width: 288px;
        padding: 5px 10px;
        text-align: center;
        display: inline-flex;
        align-items: center;
        justify-content: center;
        background: #C9FA3D;
        color: #0F0F10;
        font-size: 18px;
        font-weight: 700;
        line-height: 130%;
        letter-spacing: 0.36px;
        border: none;
        outline: none;
        transition: 0.3s;
        cursor: pointer;

        &:hover {
            box-shadow: 0px 8px 20px rgba(67, 253, 130, 0.25), 0px 26px 50px #2f463a;
        }
    }
}

.service-testimonials {
    font-family: 'Montserrat', sans-serif;
}

.testimonialslideblock {
    border-radius: 24px;
    border: 1px solid rgba(219, 255, 116, 0.20);
    background: rgba(15, 15, 16, 0.90);
    box-shadow: 6px 5px 18px 0px #000;
    padding: 112px 189px 68px;
    position: relative;

    .service-container {
        padding: 0;
    }

    &>p {
        color: #fff;
        font-size: 22px;
        font-weight: 500;
        line-height: 200%;
        margin-bottom: 48px;
        display: -webkit-box;
        -webkit-line-clamp: 4;
        -webkit-box-orient: vertical;
        overflow: hidden;
        min-height: 176px;
    }
}

.testimonialslideblock__bottsect {
    display: flex;
    align-items: center;
    flex-wrap: wrap;

    .testimonialslideblock__user {
        width: 100%;
        max-width: calc(100% - 200px);
        flex: 0 0 calc(100% - 200px);
        display: flex;
        align-items: center;

        img {
            width: 100%;
            max-width: 88px;
            flex: 0 0 88px;
            height: 88px;
            border-radius: 50%;
        }

        .testimonialslideblock__userdescr {
            width: 100%;
            max-width: calc(100% - 88px);
            flex: 0 0 calc(100% - 88px);
            padding-left: 24px;

            p {
                font-size: 24px;
                font-weight: 700;
                line-height: 135%;
                color: #4B4B4B;
            }

            .name {
                color: #fff;
            }
        }

    }

    .testimonialslideblock__rating {
        width: 100%;
        max-width: 200px;
        flex: 0 0 200px;
        text-align: right;
        display: flex;
        align-items: center;
        justify-content: flex-end;

        span {
            color: #fff;
            font-size: 22px;
            font-weight: 500;
            line-height: 200%;
            margin-bottom: -3px;
        }

        .stars {
            display: flex;
            align-items: center;
            padding-left: 8px;

            img {
                margin: 0 2px;
            }
        }
    }
}

.testimonialslider-new {

    .slick-track {
        padding-bottom: 32px;
    }

    .slick-slide {
        padding: 0 32px;
        filter: grayscale(1);
        opacity: 0.4;
        transition: 0.3s;
    }

    .slick-current.slick-active.slick-center {
        filter: grayscale(0);
        opacity: 1;
    }

    .slick-dots {
        display: flex;
        align-items: center;
        justify-content: center;
        list-style-type: none;

        button {
            color: transparent;
            width: 16px;
            height: 16px;
            border-radius: 50%;
            background: transparent;
            border: 1px solid #E7EAEC;
            margin: 0 8px;
        }

        .slick-active {
            button {
                background: linear-gradient(125.34deg, #CDFF7C 14.35%, #8CFFAC 54.63%, #93FFEB 101.27%);
                border: 1px solid transparent;
            }
        }
    }

    .slick-next.slick-arrow,
    .slick-prev.slick-arrow {
        width: 67px;
        height: 67px;
        border-radius: 100px;
        border: 1px solid #E7EAEC;
        background: #1A1A1C;
        background-repeat: no-repeat;
        background-position: center;
        bottom: initial;
        top: 50%;
        transform: translateY(-33px);
        position: absolute;
        z-index: 2;
        cursor: pointer;
    }

    .slick-next.slick-arrow {
        background-image: url('../images/services/arrslright.svg');
        right: 330px;
    }

    .slick-prev.slick-arrow {
        background-image: url('../images/services/arrslleft.svg');
        left: 330px;
    }
}

.qtop {
    position: absolute;
    top: 36px;
    left: 48px;
    width: 100px;
}

.qbot {
    position: absolute;
    right: 48px;
    bottom: 36px;
    width: 100px;
}

.wwd {
    .slick-slide[aria-hidden="true"]:not(.slick-cloned)~.slick-cloned[aria-hidden="true"] {
        filter: grayscale(1);
        opacity: 0.4;
        transition: 0.3s;
    }

    .slick-slide[aria-hidden="true"]:not([tabindex="-1"])+.slick-cloned[aria-hidden="true"] {
        filter: grayscale(0);
        opacity: 1;
    }
}


.servtitle {
    color: #fff;
    text-align: center;
    font-size: 56px;
    font-weight: 700;
    line-height: 135%;
    margin-bottom: 88px;
}

.wwd {
    padding: 44px 0 64px;
    font-family: 'Montserrat', sans-serif;

}

.wwd__tabs {
    border-radius: 24px;
    border: 1px solid rgba(219, 255, 116, 0.20);
    background: rgba(15, 15, 16, 0.90);
    box-shadow: 6px 5px 18px 0px #000;
    padding: 88px;
    display: flex;

    .wwd__tablinks {
        width: 100%;
        max-width: 40%;
        flex: 0 0 40%;

        ol {
            padding: 0;
            margin: 0;

            li {
                margin-bottom: 36px;
                font-size: 24px;
                font-weight: 700;
                line-height: 135%;
                color: #4B4B4B;
                transition: 0.3s;

                &:last-child {
                    margin-bottom: 0;
                }

                &.active {
                    color: #C9FA3D;
                }

                &:hover {
                    color: #fff;
                }
            }
        }
    }

    .wwd__tabcontents {
        width: 100%;
        max-width: 60%;
        flex: 0 0 60%;
        padding-left: 160px;
        position: relative;

        &::after {
            content: '';
            display: inline-block;
            height: 100%;
            width: 1px;
            background: url('../images/services/lineel.png') no-repeat center / 100%;
            position: absolute;
            left: 0;
            top: 0;
        }

        img {
            max-height: 88px;
            margin-bottom: 24px;
        }

        .title {
            margin-bottom: 24px;
            color: #fff;
            font-size: 32px;
            font-weight: 700;
            line-height: 140%;
        }

        .descr {
            color: #fff;
            font-size: 22px;
            font-weight: 500;
            line-height: 200%;

            p {
                &:last-child {
                    margin-bottom: 0;
                }
            }
        }

    }
}

.tabcontent {
    display: none;

    &.show {
        display: block;
    }
}

.section-subtitle {
    font-family: 'Montserrat', sans-serif;
}

.servtech {
    padding: 100px 0 64px;
    background: url('../images/services/bgtech.png') no-repeat center / 100%;
    font-family: 'Montserrat', sans-serif;

    &.active {
        .techrow .techrow__line {
            transform: translateX(0);
        }
    }
}

.service-testimonials,
.servappssect,
.wwd {
    background: url('../images/services/bgtech.png') no-repeat center / 100%;
}

.techrow {
    display: flex;
    height: 82px;
    align-items: center;
    border-radius: 8px;
    border: 1px solid rgba(201, 250, 61, 0.20);
    background-color: #141414;
    margin-bottom: 68px;
    overflow: hidden;

    &:last-child {
        margin-bottom: 0;
    }

    .techrow__percent {
        padding-right: 100px;
        display: flex;
        align-items: center;

        .pec {
            width: 100%;
            max-width: 60px;
            flex: 0 0 60px;
            color: #C9FA3D;
            font-size: 22px;
            font-style: italic;
            font-weight: 500;
            line-height: 200%;
        }

        .line {
            padding-right: 8px;
            width: 100%;
            max-width: calc(100% - 60px);
            flex: 0 0 calc(100% - 60px);

            i {
                height: 1px;
                background: linear-gradient(90deg, rgba(255, 255, 255, 0.00) 0%, #DBFF74 100%);
                display: block;
            }
        }
    }

    .techrow__line {
        display: flex;
        align-items: center;
        padding: 5px 24px;
        border-radius: 8px;
        height: 100%;
        background-image: url('../images/services/bgtechrow.png');
        background-repeat: no-repeat;
        background-size: cover;
        display: flex;
        align-items: center;
        justify-content: space-between;

        .title {
            color: #fff;
            font-size: 24px;
            font-weight: 700;
            line-height: 135%;
        }

        .techicon {
            img {
                max-width: 47px;
                max-height: 40px;
            }
        }

        transition: 1s;
        transform: translateX(100%);
    }

    &.v100 {
        .techrow__percent {
            width: 100%;
            max-width: 80%;
            flex: 0 0 80%;
        }

        .techrow__line {
            width: 100%;
            max-width: 20%;
            flex: 0 0 20%;

        }
    }

    &.v90 {
        .techrow__percent {
            width: 100%;
            max-width: 75%;
            flex: 0 0 75%;
        }

        .techrow__line {
            width: 100%;
            max-width: 35%;
            flex: 0 0 35%;

        }
    }

    &.v80 {
        .techrow__percent {
            width: 100%;
            max-width: 70%;
            flex: 0 0 70%;
        }

        .techrow__line {
            width: 100%;
            max-width: 30%;
            flex: 0 0 30%;

        }
    }

    &.v70 {
        .techrow__percent {
            width: 100%;
            max-width: 65%;
            flex: 0 0 65%;
        }

        .techrow__line {
            width: 100%;
            max-width: 35%;
            flex: 0 0 35%;

        }
    }

    &.v60 {
        .techrow__percent {
            width: 100%;
            max-width: 60%;
            flex: 0 0 60%;
        }

        .techrow__line {
            width: 100%;
            max-width: 40%;
            flex: 0 0 40%;

        }
    }

    &.v50 {
        .techrow__percent {
            width: 100%;
            max-width: 50%;
            flex: 0 0 50%;
        }

        .techrow__line {
            width: 100%;
            max-width: 50%;
            flex: 0 0 50%;

        }
    }

    &.v40 {
        .techrow__percent {
            width: 100%;
            max-width: 45%;
            flex: 0 0 45%;
        }

        .techrow__line {
            width: 100%;
            max-width: 55%;
            flex: 0 0 55%;

        }
    }

    &.v30 {
        .techrow__percent {
            width: 100%;
            max-width: 35%;
            flex: 0 0 35%;
        }

        .techrow__line {
            width: 100%;
            max-width: 65%;
            flex: 0 0 65%;

        }
    }

    &.v20 {
        .techrow__percent {
            width: 100%;
            max-width: 30%;
            flex: 0 0 30%;
        }

        .techrow__line {
            width: 100%;
            max-width: 70%;
            flex: 0 0 70%;

        }
    }

    &.v10 {
        .techrow__percent {
            width: 100%;
            max-width: 20%;
            flex: 0 0 20%;
        }

        .techrow__line {
            width: 100%;
            max-width: 80%;
            flex: 0 0 80%;

        }
    }

}

.service-workin.p0 {
    padding-bottom: 0;

    .workinsliderserv .slick-track {
        padding-bottom: 130px;
    }
}

.workinsliderserv {
    &::after {
        display: none;
    }
}

.slinfwrap.mobshow {
    display: none;
}

.rdytobecome {
    padding: 120px 0;
    position: relative;
    font-family: 'Montserrat', sans-serif;
    text-align: center;
    overflow: hidden;
    box-shadow: 2px 6px 40px 0px rgba(226, 252, 88, 0.10);
    margin-bottom: 164px;

    &::after,
    &::before {
        content: '';
        display: block;
        width: 100%;
        height: 2px;
        z-index: 2;
    }

    &::after {
        position: absolute;
        left: 0;
        bottom: 0;
        background: linear-gradient(135deg, #C9FA3D 0%, #4AFF88 100%);
    }

    &::before {
        position: absolute;
        left: 0;
        top: 0;
        background: linear-gradient(135deg, #C9FA3D 0%, #4AFF88 100%);
    }

    .rdytobecome__bg {
        position: absolute;
        left: 0;
        top: 0;
        width: 100%;
        height: 100%;
        object-fit: cover;
        object-position: 50% 50%;
    }

    h3 {
        color: #fff;
        font-size: 80px;
        font-weight: 700;
        line-height: 120%;
        max-width: 1031px;
        margin: 0 auto 64px;
    }

    .rdytobecome__link {
        font-size: 32px;
        font-weight: 700;
        line-height: 140%;


        img {
            width: 44px;
            vertical-align: middle;
            margin-top: -3px;
            transition: 0.3s;
        }

        span {
            position: relative;
            background: linear-gradient(125.34deg, #CDFF7C 0%, #8CFFAC 30.63%, #93FFEB 51%, white 50%);
            -webkit-background-clip: text;
            -webkit-text-fill-color: transparent;
            background-size: 250% 100%;
            -webkit-transition: background-position 0.5s ease;
            transition: background-position 0.5s ease;
            background-position: 0 0;

            &::after {
                content: "";
                display: inline-block;
                height: 2px;
                width: 0;
                position: absolute;
                left: 0;
                bottom: -5px;
                background: #a9ff97;
                -webkit-transition: 0.3s;
                transition: 0.3s;
            }
        }

        &:hover {
            img {
                -webkit-animation: movearr 1s infinite;
                animation: movearr 1s infinite;
            }

            span {
                &::after {
                    width: 100%;
                }
            }
        }


    }
}

@keyframes movearr {
    0% {
        transform: translate(0, 0);
    }

    50% {
        transform: translate(5px, -5px);
    }

    100% {
        transform: translate(0, 0);
    }
}

